import { useState } from "react";

const SimpleInput = (props) => {
  const [inputName, setInputName] = useState("");
  const [inputNameIsTouched, setInputNameIsTouched] = useState(false);

  const [inputEmail, setInputEmail] = useState("");
  const [inputEmailIsTouched, setInputEmailIsTouched] = useState(false);

  const inputNameIsValid = inputName.trim() !== "";
  const inputEmailIsValid = inputEmail.trim() !== "";
  const nameInputIsValid = inputNameIsTouched && !inputNameIsValid;
  const emailInputIsValid = inputEmailIsTouched && !inputEmailIsValid;

  let formIsValid = false;

  if (inputNameIsValid && inputEmailIsValid) formIsValid = true;

  const inputNameHandler = (event) => {
    setInputName(event.target.value);
  };
  const nameInputBlurHandler = () => {
    setInputNameIsTouched(true);
  };

  const inputEmailHandler = (event) => {
    setInputEmail(event.target.value);
  };
  const emailInputBlurHandler = () => {
    setInputEmailIsTouched(true);
  };

  const submitFormHandler = (event) => {
    event.preventDefault();

    setInputNameIsTouched(true);

    if (!inputNameIsValid && !inputEmailIsValid) {
      return;
    }

    setInputName("");
    setInputNameIsTouched(false);

    setInputEmail("");
    setInputEmailIsTouched(false);
  };

  const nameInputClass = nameInputIsValid
    ? "form-control invalid"
    : "form-control";

  const emailInputClass = emailInputIsValid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={submitFormHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          value={inputName}
          onBlur={nameInputBlurHandler}
          onChange={inputNameHandler}
        />
      </div>
      {nameInputIsValid && <p className="error-text">Pls Enter A Valid Name</p>}
      <div className={emailInputClass}>
        <label htmlFor="name">Your Email</label>
        <input
          type="text"
          id="email"
          value={inputEmail}
          onBlur={emailInputBlurHandler}
          onChange={inputEmailHandler}
        />
      </div>
      {nameInputIsValid && (
        <p className="error-text">Pls Enter A Valid Email</p>
      )}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
