import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const [inputName, setInputName] = useState("");
  const [inputNameIsValid, setInputNameIsValid] = useState(true);
  const nameField = useRef();

  const inputNameHandler = (event) => {
    setInputName(event.target.value);
  };

  const submitFormHandler = (event) => {
    event.preventDefault();

    if (inputName.trim() === "") {
      setInputNameIsValid(false);
      return;
    }

    setInputNameIsValid(true);
    console.log(inputName);
    const inputNameField = nameField.current.value;
    console.log(inputNameField);
    setInputName("");
  };

  const nameInputClass = inputNameIsValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={submitFormHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          ref={nameField}
          value={inputName}
          onChange={inputNameHandler}
        />
      </div>
      {!inputNameIsValid && (
        <p className="error-text">Pls Enter A Valid Name</p>
      )}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
