import { Fragment } from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { uiAction } from "./store";

let loading = false;

function App() {
  const dispatch = useDispatch();
  const toggleCart = useSelector((state) => state.ui.cartIsVisible);
  const items = useSelector((value) => value.cart);
  const notification = useSelector((value) => value.ui.notification);

  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        uiAction.setNotification({
          status: "pending",
          title: "sending...",
          message: "Sending cart data!",
        })
      );
      const response = await fetch(
        "https://react-demo-b0db3-default-rtdb.firebaseio.com/items",
        {
          method: "PUT",
          body: JSON.stringify(items),
        }
      );

      if (!response.ok) {
        throw new Error("Sending cart data failed");
      }

      dispatch(
        uiAction.setNotification({
          status: "success",
          title: "Success...",
          message: "Sent cart data!",
        })
      );
    };

    if (!loading) {
      loading = true;
      return;
    }

    sendCartData().catch((err) => {
      dispatch(
        uiAction.setNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    });
  }, [items, dispatch]);

  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {toggleCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
